import React, { Component, PropTypes } from 'react'
import { Meteor } from 'meteor/meteor'
import classnames from 'classnames'

import ChatBoxItem from './ChatBoxItem.jsx'

export default class ChatBox extends Component {
  renderChatList() {
    let msgs = this.props.msgs
    return msgs.map((msg) => {
      return (
        <ChatBoxItem
         key={msg._id}
         msg={msg}
        />
      )
    })
  }

  handleSubmit(event) {
    event.preventDefault()
    const text = ReactDOM.findDOMNode(this.refs.textInput).value.trim()

    let r_id = this.props.msgs[0].send_id === Meteor.userId() ? this.props.msgs[0].recevier_id : this.props.msgs[0].send_id
    Meteor.call('msgs.insert', text, r_id)

    ReactDOM.findDOMNode(this.refs.textInput).value = ''
  }

  render() {
    return (
      <div className="chat-box">
        <h1 className="chat-box-tit">
          { this.props.name }
        </h1>
        <ul className="chat-list">
          { this.renderChatList() }
        </ul>
        <form className="msg-send" onSubmit={this.handleSubmit.bind(this)}>
          <input type="text" ref="textInput" placeholder="Type to add new Msgs"/>
        </form>
      </div>
    )
  }
}

ChatBox.propTypes = {
  name: PropTypes.string.isRequired,
  msgs: PropTypes.array.isRequired
}
