import { Meteor } from 'meteor/meteor'
import { Mongo } from 'meteor/mongo'
import { check } from 'meteor/check'

export const Msgs = new Mongo.Collection('msgs');
if (Meteor.isServer) {
  Meteor.publish('msgs', function msgsPublication() {
    return Msgs.find({
      $or: [
        { send_id: this.userId },
        { recevier_id: this.userId },
      ],
    })
  })
} 
Meteor.methods({
  chatMsg: function(friend_id){
    console.log(this.userId, friend_id, 'xx')
    check(friend_id, String)
    return Msgs.find({}, {
      $or: [
        {send_id: this.userId, recevier_id: friend_id}, 
        {recevier_id: friend_id, send_id: this.userId}
      ] 
    }).fetch()
  } 
})
