import React, { Component, PropTypes } from 'react'
import { Meteor } from 'meteor/meteor'
import classnames from 'classnames'

import ChatBoxItem from './ChatBoxItem.jsx'

export default class ChatBox extends Component {
  renderChatList() {
    // let msgs = this.props.msgs
    let msgs = [
        {
          "_id" : "5700bde398fc9a8bfab337d4",
          "send_id" : "J7dTSDmwLs4jeqhHk",
          "recevier_id" : "fRz8Q33yA26TATE2s",
          "content" : "from 1 to 2",
          "time" : ISODate("2016-04-03T06:53:23.361Z"),
          "status" : true
        },
        {
          "_id" : "5700bde398fc9a8bfab337d5",
          "send_id" : "J7dTSDmwLs4jeqhHk",
          "recevier_id" : "fRz8Q33yA26TATE2s",
          "content" : "from 1 to 2",
          "time" : ISODate("2016-04-03T06:54:23.361Z"),
          "status" : true
        },
        {
          "_id" : "5700bde398fc9a8bfab337d6",
          "send_id" : "fRz8Q33yA26TATE2s",
          "recevier_id" : "J7dTSDmwLs4jeqhHk",
          "content" : "from 2 to 1",
          "time" : ISODate("2016-04-03T06:55:23.361Z"),
          "status" : true
        }
    ]
    const name = "Ann"
    return msgs.map((msg) => {
      return (
        <ChatBoxItem
         key={msg._id}
         msg={msg}
         name={name}
        />
      )
    })
  }

  handleSubmit(event) {
    event.preventDefault()
    const text = ReactDOM.findDOMNode(this.refs.textInput).value.trim()

    let r_id = this.props.msgs[0].send_id === Meteor.userId() ? this.props.msgs[0].recevier_id : this.props.msgs[0].send_id
    Meteor.call('msgs.insert', text, r_id)

    ReactDOM.findDOMNode(this.refs.textInput).value = ''
  }

  render() {
    return (
      <div className="chat-box">
        <h1 className="chat-box-tit">
          // { this.props.name }
          Ann
        </h1>
        <ul className="chat-list">
          { this.renderChatList() }
        </ul>
        <form className="msg-send" onSubmit={this.handleSubmit.bind(this)}>
          <input type="text" ref="textInput" placeholder="Type to add new Msgs"/>
        </form>
      </div>
    )
  }
}

