import { Meteor } from 'meteor/meteor'
import { Mongo } from 'meteor/mongo'
import { check } from 'meteor/check'

export const Msgs = new Mongo.Collection('msgs');
if (Meteor.isServer) {
  Meteor.publish('msgs', function msgsPublication() {
    console.log(this.userId)
    return Msgs.find({
      $or: [
        { send_id: this.userId },
        { recevier_id: this.userId },
      ],
    })
  })
} 
Meteor.methods({
  'msg.chatMsgs'(friend_id) {
    console.log(friend_id, this.userId, 'hello world')
    check(friend_id, String)
    return Msgs.find({ 
      $or: [
        { send_id: this.userId._str, recevier_id: friend_id._str },
        { send_id: friend_id._str, recevier_id: this.userId._str },
      ]
    })
  } 
})
