import React, { Component, PropTypes } from 'react'
import { Meteor } from 'meteor/meteor'
import classnames from 'classnames'
import { Users } from '../api/users.js'
// MsgBoxItem component - represents msg item of user's msgBox list
export default class MsgBoxItem extends Component {
  getFriendName (send_id, recevier_id) {
    let friend_id = send_id == Meteor.userId() ? recevier_id : send_id
    Meteor.call('findUser', friend_id, function(err, res) {
      console.log(res, 'haha');
      return res
    });
  }
  render () {
    const itemClassName = classnames({
      'msg-item': true,
      'msg-item--unread': this.props.msg.isUnread
    })
    const msg = this.props.msg
    console.log(msg)
    return (
      <li className="{{ itemClassName }}">
        <span className="msg-item_name"> { this.getFriendName(msg.send_id, msg.recevier_id) } </span> 
      </li>
    )
  }
}

MsgBoxItem.propTypes = {
  msg: PropTypes.object.isRequired
}
