import React, { Component, PropTypes } from 'react'
import { Meteor } from 'meteor/meteor'
import classnames from 'classnames'

export default class ChatBoxItem extends Component {
  render() {
    const msg = this.props.msg
    const friendName = this.props.msg.send_id === Meteor.userId() ? Meteor.user().username : this.props.friendName
    return (
      <li>
        <div className="chat-wrap">
          <span className="chat-name">{ friendName }</span>
          <span className="chat-time">{ `${msg.time.toLocaleDateString()}  ${msg.time.toLocaleTimeString()}` }</span>
          <p className="chat-content">{ msg.content }</p>
        </div>
      </li>
    )
  }
}

ChatBoxItem.propTypes = {
  msg: PropTypes.object.isRequired,
  friendName: PropTypes.string.isRequired
}
