import React, { Component, PropTypes } from 'react'
import { createContainer } from 'meteor/react-meteor-data'
import ReactDOM from 'react-dom'
import { Meteor } from 'meteor/meteor'
import classnames from 'classnames'
import ChatBoxItem from './ChatBoxItem.jsx'
import { Users } from "../api/users.js"
import { Msgs } from "../api/msgs.js"

class ChatBox extends Component {
  renderChatList() {
    let msgs = this.props.msgs
    let friendName = this.props.friend[0] && this.props.friend[0].username
    // if(!friendName) return
    return msgs.map((msg) => {
      return (
        <ChatBoxItem
         key={msg._id}
         msg={msg}
         friendName={friendName}
        />
      )
    })
  }

  handleSubmit(event) {
    event.preventDefault()
    const text = ReactDOM.findDOMNode(this.refs.textInput).value.trim()
    let r_id = this.props.msgs[0].send_id === Meteor.userId() ? this.props.msgs[0].recevier_id : this.props.msgs[0].send_id
    Meteor.call('msgs.insert', text, r_id)
    ReactDOM.findDOMNode(this.refs.textInput).value = ''
  }

  render() {
    return (
      <div className="chat-box">
        <h1 className="chat-box-tit">
          { this.props.friend.username }
        </h1>
        <ul className="chat-list">
          // { this.renderChatList() }
        </ul>
        <form className="msg-send" onSubmit={this.handleSubmit.bind(this)}>
          <input type="text" ref="textInput" placeholder="Type to add new Msgs"/>
        </form>
      </div>
    )
  } 
}


ChatBox.propTypes = {
  msgs: PropTypes.array.isRequired,
  friend: PropTypes.array.isRequired
}

export default createContainer(function() {
  console.log(this.location.pathname.substr(1))
  let friendId = this.location.pathname.substr(1)
  Meteor.subscribe('findUsers')
  Meteor.subscribe('chatMsgs')
  return {
    msgs: Msgs.find({
      $or: [
        {send_id: Meteor.userId(), recevier_id: friendId}, 
        {send_id: friendId, recevier_id: Meteor.userId()}
      ] 
    }).fetch(),
    friend: Users.find({
      _id: friendId
    }).fetch()
  }
}, ChatBox)
